import React, { useState, useEffect, useContext } from 'react'
import axios from 'axios'
import '../CardComponent/Card.css'
import { TemaVerisi } from '../../App'



function Card({ deger2 }) {
  const [countries, setCountries] = useState([])
  useEffect(() => {
    getMovies();
  }, [typing])

  async function getMovies() {
    const response = await fetch("https://restcountries.com/v2/all");
    const data = await response.json();
    const getData = data.results.filter(item => item.title.toLowerCase().includes(typing.toLowerCase())).map(i => {
      return {
        id: i.episode_id,
        title: i.title
      }
    })
    setCountries(getData);
  }
  // useEffect(() => {
  //   axios.get(`https://restcountries.com/v2/all`)
  //     .then(res => {
  //       console.log(res.data)
  //       setCountries(res.data)
  //     })
  // }, [])
  return (
    <div className='Card' style={useContext(TemaVerisi)}>
      {countries.map((item, index) => {
        return (
          <div className='Card_item' key={index} style={deger2}>
            <img src={item.flag} alt="img" />
            <h2>{item.name}</h2>
            <p><span>Population:</span>{item.population}</p>
            <p><span>Region:</span>{item.region}</p>
            <p><span>Capital:</span>{item.capital}</p>
          </div>)
      })}
    </div>
  )
}

export default Card
